interface EmojiPickerProps {
  isOpen: boolean
  onClose: () => void
  onEmojiSelect: (emoji: string) => void
  position?: { top?: number | string; bottom?: number | string; left?: number | string; right?: number | string }
}

export default function EmojiPicker({ isOpen, onClose, onEmojiSelect, position }: EmojiPickerProps) {
  const [searchQuery, setSearchQuery] = useState('')
  const [selectedCategoryIndex, setSelectedCategoryIndex] = useState(1) // Start with Smileys
  const [recentEmojis, setRecentEmojis] = useState<string[]>([])
  const pickerRef = useRef<HTMLDivElement>(null)

  // Load recent emojis from localStorage
  useEffect(() => {
    const stored = localStorage.getItem('recentEmojis')
    if (stored) {
      setRecentEmojis(JSON.parse(stored))
      // Update Recently Used category
      EMOJI_CATEGORIES[0].emojis = JSON.parse(stored)
    }
  }, [])

  // Save emoji to recent
  const saveToRecent = (emoji: string) => {
    const updated = [emoji, ...recentEmojis.filter(e => e !== emoji)].slice(0, 30)
    setRecentEmojis(updated)
    localStorage.setItem('recentEmojis', JSON.stringify(updated))
    EMOJI_CATEGORIES[0].emojis = updated
  }

  // Filter emojis based on search
  const filteredEmojis = useMemo(() => {
    if (!searchQuery.trim()) {
      return EMOJI_CATEGORIES[selectedCategoryIndex].emojis
    }
    // Search across all categories
    const query = searchQuery.toLowerCase()
    const allEmojis: string[] = []
    EMOJI_CATEGORIES.forEach(cat => {
      allEmojis.push(...cat.emojis)
    })
    // Simple search - in a real app, you'd use emoji names/descriptions
    return allEmojis.filter(emoji => emoji.includes(searchQuery))
  }, [searchQuery, selectedCategoryIndex])

  // Handle emoji click
  const handleEmojiClick = (emoji: string) => {
    saveToRecent(emoji)
    onEmojiSelect(emoji)
  }

  // Close on outside click
  useEffect(() => {
    if (!isOpen) return
    const handleClickOutside = (e: MouseEvent) => {
      if (pickerRef.current && !pickerRef.current.contains(e.target as Node)) {
        onClose()
      }
    }
    document.addEventListener('mousedown', handleClickOutside)
    return () => document.removeEventListener('mousedown', handleClickOutside)
  }, [isOpen, onClose])

  if (!isOpen) return null

  return (
    <div
      ref={pickerRef}
      className="absolute z-50 rounded-xl overflow-hidden mt-2"
      style={{
        background: 'rgba(17, 24, 39, 0.98)',
        border: '1px solid rgba(139, 92, 246, 0.3)',
        backdropFilter: 'blur(20px)',
        boxShadow: '0 10px 40px rgba(0, 0, 0, 0.5), 0 0 20px rgba(139, 92, 246, 0.2)',
        width: '360px',
        maxHeight: '480px',
        display: 'flex',
        flexDirection: 'column',
        top: position?.top !== undefined ? (typeof position.top === 'string' ? position.top : `${position.top}px`) : 'auto',
        bottom: position?.bottom !== undefined ? `${position.bottom}px` : 'auto',
        left: position?.left !== undefined ? `${position.left}px` : '0',
        right: position?.right !== undefined ? `${position.right}px` : 'auto',
        marginTop: '8px',
      }}
    >
      {/* Search Bar */}
      <div className="p-3 border-b border-purple-500/20">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400" />
          <input
            type="text"
            placeholder="Search"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-10 pr-4 py-2 bg-white/10 border border-gray-600 rounded-lg text-white placeholder-gray-400 text-sm font-exo2 focus:outline-none focus:ring-1 focus:ring-purple-500"
            autoFocus
          />
        </div>
      </div>

      {/* Category Tabs */}
      <div className="flex items-center gap-2 p-2 border-b border-purple-500/20 overflow-x-auto scrollbar-hide">
        {EMOJI_CATEGORIES.map((category, index) => {
          const Icon = category.icon
          return (
            <button
              key={category.name}
              onClick={() => {
                setSelectedCategoryIndex(index)
                setSearchQuery('')
              }}
              className={`flex-shrink-0 w-10 h-10 rounded-full flex items-center justify-center transition-all ${
                selectedCategoryIndex === index
                  ? 'bg-purple-500/30 border-2 border-purple-400'
                  : 'bg-white/5 border-2 border-transparent hover:bg-white/10'
              }`}
              title={category.name}
            >
              <Icon className={`w-5 h-5 ${selectedCategoryIndex === index ? 'text-purple-300' : 'text-gray-400'}`} />
            </button>
          )
        })}
      </div>

      {/* Category Title */}
      <div className="px-4 py-2 bg-gray-800/50 border-b border-purple-500/10">
        <h3 className="text-sm font-exo2 text-gray-300">
          {EMOJI_CATEGORIES[selectedCategoryIndex].name}
        </h3>
      </div>

      {/* Emoji Grid */}
      <div className="flex-1 overflow-y-auto scrollbar-hide p-3" style={{ maxHeight: '320px' }}>
        <div className="grid grid-cols-8 gap-2">
          {filteredEmojis.map((emoji, index) => (
            <button
              key={`${emoji}-${index}`}
              onClick={() => handleEmojiClick(emoji)}
              className="w-10 h-10 flex items-center justify-center text-2xl hover:bg-purple-500/20 rounded-lg transition-all hover:scale-110 active:scale-95"
              title={emoji}
            >
              {emoji}
            </button>
          ))}
        </div>
        {filteredEmojis.length === 0 && (
          <div className="text-center text-gray-400 py-8 text-sm font-exo2">
            No emojis found
          </div>
        )}
      </div>

      {/* What's Your Mood Section */}
      <div className="p-4 bg-purple-900/20 border-t border-purple-500/20">
        <div className="flex items-center gap-3">
          <span className="text-3xl">ðŸ˜Š</span>
          <span className="text-sm text-gray-300 font-exo2">What's Your Mood?</span>
        </div>
      </div>
    </div>
  )
}

